// Clean JSON Response function for n8n
// This function handles various formats of AI-generated JSON responses
// and returns a clean, parseable JSON object

function cleanJSONResponse(response) {
  try {
    // Extract the output property if it exists
    const output = response.output || response;

    let jsonStr = output;

    // If output is a string, clean it up
    if (typeof jsonStr === 'string') {
      // Remove markdown code block formatting if present
      jsonStr = jsonStr.replace(/```json\n|\n```|```/g, '');

      // Attempt to find a JSON object in the text
      const jsonMatch = jsonStr.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        jsonStr = jsonMatch[0];
      }
    }

    // Handle the case where the response is already a valid object
    if (typeof jsonStr === 'object' && jsonStr !== null && !Array.isArray(jsonStr)) {
      return jsonStr;
    }

    // Parse the JSON string to object
    const parsedJson = JSON.parse(jsonStr);

    // Return the cleaned JSON
    return parsedJson;
  } catch (error) {
    // Return error information if parsing fails
    return { 
      error: "Failed to parse JSON", 
      message: error.message,
      originalOutput: response
    };
  }
}

// Get the response from the previous node
const cleanedResponse = cleanJSONResponse($json);

// Return the clean JSON
return {
  json: cleanedResponse
};

